mapper.update:
update projects
set deleted = deleted + 1
where projects.projectId = #{projectId}

update projects
set deleted = 0
where projectId = #{id}

mapper.select:
select *
from projects a,
     group_project b
where a.projectId = b.projectId
  and a.deleted = #{deleted}
  and b.groupId = #{id}

select *
from projects a,
     group_project b
where a.projectId = b.projectId
  and b.groupId = #{groupId}
  and a.createTime > #{start}
  and a.createTime < #{end}
  and a.deleted = #{deleted}
  and (projectName like #{word}
    or projectDescription like #{word})
